'.slider-img', {

    loop: false,
    speed: 2400,
    parallax: true,

    navigation: {
        prevEl: '.swiper-button-prev',
        nextEl: '.swiper-button-next'
    },

    pagination: {
        el: '.swiper-pagination',
        type: 'bullets',
        clickable: true,
    },

    scrollbar: {
        el: '.swiper-scrollbar',
        draggable: true,
    },

    mousewheel: {
        invert: false
    },

    mousewheel: {
        sensitivity: 1,
    },

    keyboard: {
        enabled: true,
        onlyInViewport: true,
        pageUpDown: true,
    },
}